import { JwtConfig } from "../../../../types/src";
/**
 * Defines the standard pagination parameters
 * supported by the ledger API.
 */
export type LedgerPaginationParams = {
    /**
     * Current page index, zero based.
     */
    index: number;
    /**
     * Page results limit, max page size.
     */
    limit: number;
};
export type LedgerListParams = {
    page?: LedgerPaginationParams;
    authParams?: Partial<JwtConfig>;
    ['meta.moment']?: Date;
    ['meta.moment.$eq']?: Date;
    ['meta.moment.$lt']?: Date;
    ['meta.moment.$gt']?: Date;
};
