import { LedgerAddress } from '../common/ledger-address';
import { LedgerWallet } from './ledger-wallet';
/**
 * This record represents domain wallets.
 * They can be stored in the ledger or be part
 * of wallets stored in ledger.
 * Wallet addresses are represented by:
 *   - schema:identifier@domain
 */
export type LedgerDomain = Partial<LedgerWallet> & {
    /**
     * Domain handle
     */
    handle: LedgerAddress;
};
